import React, { useState } from 'react';
import Navbar from '../../Components/Navbar/Navbar'
import './Login.css'

const Login = () => {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [successMessage, setSuccessMessage] = useState('');


    const handleUsernameChange = (e) => {
        setUsername(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Add logic to handle form submission (e.g., send login request to server)
        console.log('Username:', username);
        console.log('Password:', password);

        e.preventDefault();
        fetch('/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ username, password })
        })
        .then(response => {
            if (response.ok) {
                return response.json();
            }
            throw new Error('Login failed');
        })
        .then(data => {
            // Login successful, handle redirection or other actions
            console.log('Login successful:', data);
        })
        .catch(error => {
            console.error('Error logging in:', error);
            setError('Login failed');
        });
    };




  return (
    <>
        <div id='main'>
            <Navbar/>

            <div className="login-container">
                <h2>Login</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        {/* <label htmlFor="email">Email:</label> */}
                        <input type="text" id="username" name="username" placeholder='Your Username' value={username}
                        onChange={handleUsernameChange}/>
                    </div>
                    <div className="form-group">
                        {/* <label htmlFor="password">Password:</label> */}
                        <input type="password" id="password" name="password" placeholder='Your Password' value={password}
                        onChange={handlePasswordChange}/>
                    </div>
                    <button type="submit" >Login</button>
               </form>
            </div>

            
            {successMessage && (
                        <div className='successMessage'>
                            <p>{successMessage}</p>
                            <Link to="/">Go to Homepage</Link>
                        </div>
            )}



            {/* <Footer/> */}
        </div>
    </>
  )
}

export default Login
